import axios from "axios"
import { useState, useEffect } from "react"


const Country = ({country, showDetails}) => {

  const [showDet, setshowDet] = useState(showDetails)
  const [weather, setweather] = useState({})

  const detailToggle = () => {
    setshowDet(!showDet)
  }

  useEffect(() => {
    setshowDet(showDetails)
  }, [showDetails])

  useEffect(() => {
    axios
      .get('http://api.weatherapi.com/v1/current.json', {params: {key: process.env.WEATHER_API_KEY, q: country.name}})
      .then((res)=>{

      })
  }, [])

  return (
    <div>
      <h2>{country.name}</h2>
      <button onClick={detailToggle}>show details</button>

      { showDet &&
        <div className="country-details">
          <div>{country.capital}</div>
          <div>{country.area}</div>
          <h3>Languages</h3>
          <ul>
            {country.languages.map((l)=><li key={l.name}>{l.name}</li>)}
          </ul>
          <div>
            <img src={country.flag}/>
          </div>

          <div>
            <h3>Weather</h3>
          </div>
        </div>
      }
    </div>
  )
}

export default Country
