import { useState } from "react"


const Country = ({country, showDetails}) => {

  const [showDetails_, setshowDetails_] = useState(showDetails)
  const detailToggle = () => {
    setshowDetails_(!showDetails_)
  }

  console.log(showDetails_);

  return (
    <div>
      <h2>{country.name}</h2>
      <button onClick={detailToggle}>show details</button>

      { showDetails_ &&
        <div className="country-details">
          <div>{country.capital}</div>
          <div>{country.area}</div>
          <h3>Languages</h3>
          <ul>
            {country.languages.map((l)=><li>{l.name}</li>)}
          </ul>
          <div>
            <img src={country.flag}/>
          </div>
        </div>
      }
    </div>
  )
}

export default Country
